mapper.select:
SELECT m_motorcycle.motorcycle_no, m_motorcycle.motorcycle_name, m_motorcycle.cylinders, m_motorcycle.cooling, m_motorcycle.price, m_motorcycle.comment, m_brand.brand_id, m_brand.brand_name FROM m_motorcycle LEFT OUTER JOIN m_brand ON m_motorcycle.brand_id = m_brand.brand_id
AND m_brand.brand_id = #{brandId}
AND m_motorcycle.motorcycle_name LIKE CONCAT("%", #{keyword}, "%")
ORDER BY motorcycle_no;
